mapper.delete:
delete
from pd
where pdid = #{pdid}
  and expire_time > now()
  and p_uid = #{p_uid}

mapper.select:
select qid, p_id, v_uid, uname, amount, total_price, remark
from quote
         inner join vendor v on quote.v_uid = v.uid
where p_id = #{pdid}
limit #{size} offset #{offset}

select count(*)
from pd
where pdid = #{pdid}
  and p_uid = #{p_uid}

select count(*)
from quote
where p_id = #{pdid}

mapper.delete:
delete
from quote
where p_id = #{pdid}
  and qid != #{qid}

mapper.update:
update pd
set expire_time = now()
where pdid = #{pdid}
  and p_uid = #{p_uid}

mapper.select:
select pdid
from pd
         inner join quote on pd.pdid = quote.p_id
where qid = #{qid}
  and p_uid = #{uid}

select status
from processing
where pid = #{pid}
  and p_uid = #{uid}

select cpath as path, cname as name
from procurement
where pid = #{pid}

select count(*)
from processing
where pid = #{pid}
  and p_uid = #{uid}
  and status > 0

mapper.update:
update procurement
set status = #{current_status} + 1
where pid = #{pid}
  and status = #{current_status}

update procurement
set status = -1
where pid = #{pid}
