mapper.delete:
delete
from quote
where qid = #{qid}
  and v_uid = #{v_uid}

mapper.select:
select count(*)
from processing
where qid = #{qid}

select status
from processing
where pid = #{pid}
  and v_uid = #{uid}

select cpath as path, cname as name
from procurement
where pid = #{pid}

select count(*)
from processing
where pid = #{pid}
  and v_uid = #{uid}

mapper.update:
update procurement
set status = #{current_status} + 1
where pid = #{pid}
  and status = #{current_status}

update procurement
set status = -1
where pid = #{pid}
